contracts/UAE/UAECompliance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title UAECompliance
 * @dev Compliance contract for UAE real estate regulations
 * @dev Handles RERA, DLD, and other regulatory requirements
 */
contract UAECompliance is AccessControl, Pausable, ReentrancyGuard {

    // =============================================================================
    // ROLES
    // =============================================================================

    bytes32 public constant RERA_OFFICER_ROLE = keccak256("RERA_OFFICER_ROLE");
    bytes32 public constant DLD_OFFICER_ROLE = keccak256("DLD_OFFICER_ROLE");
    bytes32 public constant COMPLIANCE_OFFICER_ROLE = keccak256("COMPLIANCE_OFFICER_ROLE");
    bytes32 public constant KYC_OFFICER_ROLE = keccak256("KYC_OFFICER_ROLE");
    bytes32 public constant AML_OFFICER_ROLE = keccak256("AML_OFFICER_ROLE");

    // =============================================================================
    // STATE VARIABLES
    // =============================================================================

    // RERA compliance tracking
    struct RERACompliance {
        string registrationNumber;
        uint256 expiryDate;
        bool isActive;
        string projectType;
        address developer;
        uint256 lastUpdated;
    }

    // DLD compliance tracking
    struct DLDCompliance {
        string titleDeedNumber;
        string plotNumber;
        string district;
        bool isRegistered;
        uint256 registrationDate;
        uint256 lastUpdated;
    }

    // KYC compliance levels
    enum KYCLevel {
        NONE,
        BASIC,
        STANDARD,
        ENHANCED,
        COMPREHENSIVE
    }

    // AML risk levels
    enum AMLRisk {
        LOW,
        MEDIUM,
        HIGH,
        PROHIBITED
    }

    // User compliance data
    struct UserCompliance {
        KYCLevel kycLevel;
        AMLRisk amlRisk;
        bool isApproved;
        uint256 approvalDate;
        uint256 expiryDate;
        string emirate;
        bool isUAEResident;
        bool isGCCResident;
        mapping(string => bool) documents;
    }

    // Property compliance mappings
    mapping(uint256 => RERACompliance) public reraCompliance;
    mapping(uint256 => DLDCompliance) public dldCompliance;
    mapping(string => uint256) public reraToPropertyId;
    mapping(string => uint256) public dldToPropertyId;

    // User compliance mappings
    mapping(address => UserCompliance) public userCompliance;
    mapping(address => bool) public verifiedDevelopers;
    mapping(address => bool) public authorizedAgents;

    // Investment limits based on compliance
    mapping(KYCLevel => uint256) public maxInvestmentLimits;
    mapping(AMLRisk => bool) public allowedInvestmentRisk;

    // Document requirements
    mapping(KYCLevel => string[]) public requiredDocuments;

    // =============================================================================
    // EVENTS
    // =============================================================================

    event RERAComplianceUpdated(
        uint256 indexed propertyId,
        string registrationNumber,
        bool isActive
    );

    event DLDComplianceUpdated(
        uint256 indexed propertyId,
        string titleDeedNumber,
        bool isRegistered
    );

    event UserKYCUpdated(
        address indexed user,
        KYCLevel oldLevel,
        KYCLevel newLevel
    );

    event UserAMLUpdated(
        address indexed user,
        AMLRisk oldRisk,
        AMLRisk newRisk
    );

    event DeveloperVerified(
        address indexed developer,
        bool verified
    );

    event ComplianceCheckFailed(
        address indexed user,
        uint256 propertyId,
        string reason
    );

    // =============================================================================
    // CONSTRUCTOR
    // =============================================================================

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(RERA_OFFICER_ROLE, msg.sender);
        _grantRole(DLD_OFFICER_ROLE, msg.sender);
        _grantRole(COMPLIANCE_OFFICER_ROLE, msg.sender);
        _grantRole(KYC_OFFICER_ROLE, msg.sender);
        _grantRole(AML_OFFICER_ROLE, msg.sender);

        // Initialize investment limits (in AED wei)
        maxInvestmentLimits[KYCLevel.BASIC] = 50000 * 1e18;          // 50K AED
        maxInvestmentLimits[KYCLevel.STANDARD] = 500000 * 1e18;      // 500K AED
        maxInvestmentLimits[KYCLevel.ENHANCED] = 2000000 * 1e18;     // 2M AED
        maxInvestmentLimits[KYCLevel.COMPREHENSIVE] = type(uint256).max; // No limit

        // Initialize risk allowances
        allowedInvestmentRisk[AMLRisk.LOW] = true;
        allowedInvestmentRisk[AMLRisk.MEDIUM] = true;
        allowedInvestmentRisk[AMLRisk.HIGH] = false;
        allowedInvestmentRisk[AMLRisk.PROHIBITED] = false;
    }

    // =============================================================================
    // RERA COMPLIANCE FUNCTIONS
    // =============================================================================

    /**
     * @dev Update RERA compliance for a property
     */
    function updateRERACompliance(
        uint256 propertyId,
        string memory registrationNumber,
        uint256 expiryDate,
        bool isActive,
        string memory projectType,
        address developer
    ) external onlyRole(RERA_OFFICER_ROLE) {
        require(propertyId > 0, "Invalid property ID");
        require(bytes(registrationNumber).length > 0, "Invalid registration number");
        require(expiryDate > block.timestamp, "Expiry date must be in future");

        RERACompliance storage compliance = reraCompliance[propertyId];
        compliance.registrationNumber = registrationNumber;
        compliance.expiryDate = expiryDate;
        compliance.isActive = isActive;
        compliance.projectType = projectType;
        compliance.developer = developer;
        compliance.lastUpdated = block.timestamp;

        reraToPropertyId[registrationNumber] = propertyId;

        emit RERAComplianceUpdated(propertyId, registrationNumber, isActive);
    }

    /**
     * @dev Check if property has valid RERA compliance
     */
    function checkRERACompliance(uint256 propertyId) external view returns (bool isCompliant) {
        RERACompliance memory compliance = reraCompliance[propertyId];
        return compliance.isActive && compliance.expiryDate > block.timestamp;
    }

    // =============================================================================
    // DLD COMPLIANCE FUNCTIONS
    // =============================================================================

    /**
     * @dev Update DLD compliance for a property
     */
    function updateDLDCompliance(
        uint256 propertyId,
        string memory titleDeedNumber,
        string memory plotNumber,
        string memory district,
        bool isRegistered
    ) external onlyRole(DLD_OFFICER_ROLE) {
        require(propertyId > 0, "Invalid property ID");
        require(bytes(titleDeedNumber).length > 0, "Invalid title deed number");

        DLDCompliance storage compliance = dldCompliance[propertyId];
        compliance.titleDeedNumber = titleDeedNumber;
        compliance.plotNumber = plotNumber;
        compliance.district = district;
        compliance.isRegistered = isRegistered;
        compliance.registrationDate = block.timestamp;
        compliance.lastUpdated = block.timestamp;

        dldToPropertyId[titleDeedNumber] = propertyId;

        emit DLDComplianceUpdated(propertyId, titleDeedNumber, isRegistered);
    }

    /**
     * @dev Check if property has valid DLD compliance
     */
    function checkDLDCompliance(uint256 propertyId) external view returns (bool isCompliant) {
        return dldCompliance[propertyId].isRegistered;
    }

    // =============================================================================
    // KYC FUNCTIONS
    // =============================================================================

    /**
     * @dev Update user KYC level
     */
    function updateUserKYC(
        address user,
        KYCLevel newLevel,
        uint256 expiryDate,
        string memory emirate,
        bool isUAEResident,
        bool isGCCResident
    ) external onlyRole(KYC_OFFICER_ROLE) {
        require(user != address(0), "Invalid user address");
        require(expiryDate > block.timestamp, "Expiry date must be in future");

        UserCompliance storage compliance = userCompliance[user];
        KYCLevel oldLevel = compliance.kycLevel;

        compliance.kycLevel = newLevel;
        compliance.approvalDate = block.timestamp;
        compliance.expiryDate = expiryDate;
        compliance.emirate = emirate;
        compliance.isUAEResident = isUAEResident;
        compliance.isGCCResident = isGCCResident;
        compliance.isApproved = true;

        emit UserKYCUpdated(user, oldLevel, newLevel);
    }

    /**
     * @dev Check if user has valid KYC
     */
    function checkUserKYC(address user) external view returns (bool isValid, KYCLevel level) {
        UserCompliance storage compliance = userCompliance[user];
        isValid = compliance.isApproved && compliance.expiryDate > block.timestamp;
        level = compliance.kycLevel;
    }

    // =============================================================================
    // AML FUNCTIONS
    // =============================================================================

    /**
     * @dev Update user AML risk level
     */
    function updateUserAML(
        address user,
        AMLRisk newRisk
    ) external onlyRole(AML_OFFICER_ROLE) {
        require(user != address(0), "Invalid user address");

        UserCompliance storage compliance = userCompliance[user];
        AMLRisk oldRisk = compliance.amlRisk;

        compliance.amlRisk = newRisk;

        emit UserAMLUpdated(user, oldRisk, newRisk);
    }

    /**
     * @dev Check if user has acceptable AML risk
     */
    function checkUserAML(address user) external view returns (bool isAcceptable, AMLRisk risk) {
        UserCompliance storage compliance = userCompliance[user];
        risk = compliance.amlRisk;
        isAcceptable = allowedInvestmentRisk[risk];
    }

    // =============================================================================
    // INVESTMENT VALIDATION
    // =============================================================================

    /**
     * @dev Validate investment compliance
     */
    function validateInvestment(
        address user,
        uint256 propertyId,
        uint256 amount
    ) external view returns (bool isValid, string memory reason) {
        // Check property compliance
        if (!this.checkRERACompliance(propertyId)) {
            return (false, "Property not RERA compliant");
        }

        if (!this.checkDLDCompliance(propertyId)) {
            return (false, "Property not DLD registered");
        }

        // Check user compliance
        UserCompliance storage compliance = userCompliance[user];

        if (!compliance.isApproved || compliance.expiryDate <= block.timestamp) {
            return (false, "User KYC expired or not approved");
        }

        if (!allowedInvestmentRisk[compliance.amlRisk]) {
            return (false, "User AML risk too high");
        }

        // Check investment limits
        if (amount > maxInvestmentLimits[compliance.kycLevel]) {
            return (false, "Investment amount exceeds KYC limit");
        }

        return (true, "Investment validated");
    }

    // =============================================================================
    // DEVELOPER VERIFICATION
    // =============================================================================

    /**
     * @dev Verify developer
     */
    function verifyDeveloper(
        address developer,
        bool verified
    ) external onlyRole(RERA_OFFICER_ROLE) {
        verifiedDevelopers[developer] = verified;
        emit DeveloperVerified(developer, verified);
    }

    /**
     * @dev Check if developer is verified
     */
    function isDeveloperVerified(address developer) external view returns (bool) {
        return verifiedDevelopers[developer];
    }

    // =============================================================================
    // DOCUMENT MANAGEMENT
    // =============================================================================

    /**
     * @dev Mark document as submitted for user
     */
    function submitDocument(
        address user,
        string memory documentType,
        bool isValid
    ) external onlyRole(KYC_OFFICER_ROLE) {
        userCompliance[user].documents[documentType] = isValid;
    }

    /**
     * @dev Check if user has submitted required documents
     */
    function hasRequiredDocuments(
        address user,
        KYCLevel requiredLevel
    ) external view returns (bool hasAll) {
        string[] memory required = requiredDocuments[requiredLevel];
        UserCompliance storage compliance = userCompliance[user];

        for (uint256 i = 0; i < required.length; i++) {
            if (!compliance.documents[required[i]]) {
                return false;
            }
        }
        return true;
    }

    // =============================================================================
    // ADMIN FUNCTIONS
    // =============================================================================

    /**
     * @dev Set investment limit for KYC level
     */
    function setInvestmentLimit(
        KYCLevel level,
        uint256 limit
    ) external onlyRole(COMPLIANCE_OFFICER_ROLE) {
        maxInvestmentLimits[level] = limit;
    }

    /**
     * @dev Set AML risk allowance
     */
    function setAMLRiskAllowance(
        AMLRisk risk,
        bool allowed
    ) external onlyRole(COMPLIANCE_OFFICER_ROLE) {
        allowedInvestmentRisk[risk] = allowed;
    }

    /**
     * @dev Set required documents for KYC level
     */
    function setRequiredDocuments(
        KYCLevel level,
        string[] memory documents
    ) external onlyRole(COMPLIANCE_OFFICER_ROLE) {
        requiredDocuments[level] = documents;
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    // =============================================================================
    // VIEW FUNCTIONS
    // =============================================================================

    /**
     * @dev Get user compliance summary
     */
    function getUserComplianceSummary(address user) external view returns (
        KYCLevel kycLevel,
        AMLRisk amlRisk,
        bool isApproved,
        uint256 maxInvestment,
        string memory emirate,
        bool isUAEResident
    ) {
        UserCompliance storage compliance = userCompliance[user];
        return (
            compliance.kycLevel,
            compliance.amlRisk,
            compliance.isApproved && compliance.expiryDate > block.timestamp,
            maxInvestmentLimits[compliance.kycLevel],
            compliance.emirate,
            compliance.isUAEResident
        );
    }

    /**
     * @dev Get property compliance summary
     */
    function getPropertyComplianceSummary(uint256 propertyId) external view returns (
        bool reraCompliant,
        bool dldCompliant,
        string memory reraNumber,
        string memory dldNumber,
        address developer
    ) {
        RERACompliance memory rera = reraCompliance[propertyId];
        DLDCompliance memory dld = dldCompliance[propertyId];

        return (
            rera.isActive && rera.expiryDate > block.timestamp,
            dld.isRegistered,
            rera.registrationNumber,
            dld.titleDeedNumber,
            rera.developer
        );
    }
}

contracts/UAE/UAEPriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**
 * @title UAEPriceOracle
 * @dev Oracle contract for AED exchange rates and UAE property prices
 * @dev Supports multi-currency conversion and property valuation
 */
contract UAEPriceOracle is Ownable, Pausable {
    using SafeMath for uint256;

    // =============================================================================
    // STATE VARIABLES
    // =============================================================================

    uint256 public constant PRECISION = 1e18;
    uint256 public constant MAX_STALENESS = 24 hours; // Maximum price staleness

    // Exchange rates (AED to other currencies, 18 decimals)
    struct ExchangeRate {
        uint256 rate;           // Rate with 18 decimals (1 AED = rate * currency)
        uint256 lastUpdated;    // Timestamp of last update
        bool isActive;          // Whether this rate is active
        address updater;        // Who can update this rate
    }

    // Property pricing data
    struct PropertyPrice {
        uint256 pricePerSqFt;   // Price per square foot in AED wei
        uint256 totalValue;     // Total property value in AED wei
        uint256 lastUpdated;    // Last price update timestamp
        string zone;            // Dubai zone or area
        string emirate;         // UAE emirate
        bool isVerified;        // RERA/DLD verification status
    }

    // Currency exchange rates
    mapping(string => ExchangeRate) public exchangeRates;
    mapping(address => bool) public authorizedUpdaters;

    // Property pricing
    mapping(uint256 => PropertyPrice) public propertyPrices;
    mapping(string => uint256[]) public zoneProperties; // Zone to property IDs
    mapping(string => uint256) public averageZonePrices; // Average price per zone

    // Market data
    mapping(string => uint256) public marketCapitalization; // Per emirate
    mapping(string => uint256) public tradingVolume24h;     // Per currency pair

    // Oracle configuration
    string[] public supportedCurrencies;
    mapping(string => bool) public currencySupported;

    // Events
    event ExchangeRateUpdated(string indexed currency, uint256 newRate, uint256 timestamp);
    event PropertyPriceUpdated(uint256 indexed propertyId, uint256 newPrice, string zone);
    event AuthorizedUpdaterAdded(address indexed updater, string currency);
    event AuthorizedUpdaterRemoved(address indexed updater);
    event MarketDataUpdated(string indexed dataType, string key, uint256 value);

    // =============================================================================
    // CONSTRUCTOR
    // =============================================================================

    constructor() {
        // Initialize supported currencies
        _addSupportedCurrency("USD", 272e15);  // 1 AED = 0.272 USD
        _addSupportedCurrency("EUR", 248e15);  // 1 AED = 0.248 EUR
        _addSupportedCurrency("GBP", 214e15);  // 1 AED = 0.214 GBP
        _addSupportedCurrency("SAR", 102e16);  // 1 AED = 1.02 SAR
        _addSupportedCurrency("QAR", 99e16);   // 1 AED = 0.99 QAR
        _addSupportedCurrency("KWD", 83e15);   // 1 AED = 0.083 KWD
        _addSupportedCurrency("OMR", 105e15);  // 1 AED = 0.105 OMR
        _addSupportedCurrency("BHD", 103e15);  // 1 AED = 0.103 BHD
    }

    // =============================================================================
    // MODIFIERS
    // =============================================================================

    modifier onlyAuthorizedUpdater(string memory currency) {
        require(
            authorizedUpdaters[msg.sender] || 
            exchangeRates[currency].updater == msg.sender || 
            msg.sender == owner(),
            "Unauthorized updater"
        );
        _;
    }

    modifier validCurrency(string memory currency) {
        require(currencySupported[currency], "Currency not supported");
        _;
    }

    modifier notStale(uint256 lastUpdated) {
        require(block.timestamp.sub(lastUpdated) <= MAX_STALENESS, "Price data is stale");
        _;
    }

    // =============================================================================
    // EXCHANGE RATE FUNCTIONS
    // =============================================================================

    /**
     * @dev Update exchange rate for a specific currency
     * @param currency Currency code (USD, EUR, etc.)
     * @param rate New exchange rate (18 decimals)
     */
    function updateExchangeRate(
        string memory currency, 
        uint256 rate
    ) external onlyAuthorizedUpdater(currency) validCurrency(currency) whenNotPaused {
        require(rate > 0, "Invalid exchange rate");

        exchangeRates[currency].rate = rate;
        exchangeRates[currency].lastUpdated = block.timestamp;
        exchangeRates[currency].isActive = true;

        emit ExchangeRateUpdated(currency, rate, block.timestamp);
    }

    /**
     * @dev Batch update multiple exchange rates
     * @param currencies Array of currency codes
     * @param rates Array of corresponding rates
     */
    function batchUpdateExchangeRates(
        string[] memory currencies,
        uint256[] memory rates
    ) external onlyOwner whenNotPaused {
        require(currencies.length == rates.length, "Arrays length mismatch");

        for (uint256 i = 0; i < currencies.length; i++) {
            require(currencySupported[currencies[i]], "Currency not supported");
            require(rates[i] > 0, "Invalid exchange rate");

            exchangeRates[currencies[i]].rate = rates[i];
            exchangeRates[currencies[i]].lastUpdated = block.timestamp;
            exchangeRates[currencies[i]].isActive = true;

            emit ExchangeRateUpdated(currencies[i], rates[i], block.timestamp);
        }
    }

    /**
     * @dev Get current exchange rate for a currency
     * @param currency Currency code
     * @return rate Current exchange rate
     * @return lastUpdated Timestamp of last update
     * @return isActive Whether the rate is active
     */
    function getExchangeRate(string memory currency) external view validCurrency(currency) returns (
        uint256 rate,
        uint256 lastUpdated,
        bool isActive
    ) {
        ExchangeRate memory rateData = exchangeRates[currency];
        return (rateData.rate, rateData.lastUpdated, rateData.isActive);
    }

    /**
     * @dev Convert AED amount to another currency
     * @param aedAmount Amount in AED wei
     * @param targetCurrency Target currency code
     * @return convertedAmount Amount converted to target currency
     */
    function convertFromAED(
        uint256 aedAmount,
        string memory targetCurrency
    ) external view validCurrency(targetCurrency) returns (uint256 convertedAmount) {
        ExchangeRate memory rateData = exchangeRates[targetCurrency];
        require(rateData.isActive, "Exchange rate not active");

        return aedAmount.mul(rateData.rate).div(PRECISION);
    }

    /**
     * @dev Convert from another currency to AED
     * @param amount Amount in source currency wei
     * @param sourceCurrency Source currency code
     * @return aedAmount Amount converted to AED
     */
    function convertToAED(
        uint256 amount,
        string memory sourceCurrency
    ) external view validCurrency(sourceCurrency) returns (uint256 aedAmount) {
        ExchangeRate memory rateData = exchangeRates[sourceCurrency];
        require(rateData.isActive, "Exchange rate not active");

        return amount.mul(PRECISION).div(rateData.rate);
    }

    // =============================================================================
    // PROPERTY PRICING FUNCTIONS
    // =============================================================================

    /**
     * @dev Update property price information
     * @param propertyId Unique property identifier
     * @param pricePerSqFt Price per square foot in AED wei
     * @param totalValue Total property value in AED wei
     * @param zone Property zone/area
     * @param emirate UAE emirate
     * @param isVerified RERA/DLD verification status
     */
    function updatePropertyPrice(
        uint256 propertyId,
        uint256 pricePerSqFt,
        uint256 totalValue,
        string memory zone,
        string memory emirate,
        bool isVerified
    ) external onlyOwner {
        require(propertyId > 0, "Invalid property ID");
        require(pricePerSqFt > 0, "Invalid price per sq ft");
        require(totalValue > 0, "Invalid total value");

        PropertyPrice storage price = propertyPrices[propertyId];
        price.pricePerSqFt = pricePerSqFt;
        price.totalValue = totalValue;
        price.lastUpdated = block.timestamp;
        price.zone = zone;
        price.emirate = emirate;
        price.isVerified = isVerified;

        // Update zone properties tracking
        zoneProperties[zone].push(propertyId);
        _updateAverageZonePrice(zone);

        emit PropertyPriceUpdated(propertyId, totalValue, zone);
    }

    /**
     * @dev Get property price information
     * @param propertyId Property identifier
     * @return pricePerSqFt Price per square foot in AED wei
     * @return totalValue Total property value in AED wei
     * @return lastUpdated Last update timestamp
     * @return zone Property zone
     * @return emirate Property emirate
     * @return isVerified Verification status
     */
    function getPropertyPrice(uint256 propertyId) external view returns (
        uint256 pricePerSqFt,
        uint256 totalValue,
        uint256 lastUpdated,
        string memory zone,
        string memory emirate,
        bool isVerified
    ) {
        PropertyPrice memory price = propertyPrices[propertyId];
        return (
            price.pricePerSqFt,
            price.totalValue,
            price.lastUpdated,
            price.zone,
            price.emirate,
            price.isVerified
        );
    }

    /**
     * @dev Get property value in different currency
     * @param propertyId Property identifier
     * @param currency Target currency
     * @return value Property value in target currency
     */
    function getPropertyValueInCurrency(
        uint256 propertyId,
        string memory currency
    ) external view validCurrency(currency) returns (uint256 value) {
        PropertyPrice memory price = propertyPrices[propertyId];
        require(price.totalValue > 0, "Property price not set");

        if (keccak256(bytes(currency)) == keccak256(bytes("AED"))) {
            return price.totalValue;
        }

        ExchangeRate memory rateData = exchangeRates[currency];
        require(rateData.isActive, "Exchange rate not active");

        return price.totalValue.mul(rateData.rate).div(PRECISION);
    }

    /**
     * @dev Update average zone price
     */
    function _updateAverageZonePrice(string memory zone) private {
        uint256[] memory properties = zoneProperties[zone];
        if (properties.length == 0) return;

        uint256 totalValue = 0;
        uint256 validProperties = 0;

        for (uint256 i = 0; i < properties.length; i++) {
            PropertyPrice memory price = propertyPrices[properties[i]];
            if (price.totalValue > 0) {
                totalValue = totalValue.add(price.totalValue);
                validProperties++;
            }
        }

        if (validProperties > 0) {
            averageZonePrices[zone] = totalValue.div(validProperties);
        }
    }

    /**
     * @dev Get average price for a zone
     * @param zone Zone identifier
     * @return averagePrice Average property price in the zone
     */
    function getAverageZonePrice(string memory zone) external view returns (uint256 averagePrice) {
        return averageZonePrices[zone];
    }

    // =============================================================================
    // MARKET DATA FUNCTIONS
    // =============================================================================

    /**
     * @dev Update market capitalization for an emirate
     * @param emirate Emirate name
     * @param marketCap Market capitalization in AED wei
     */
    function updateMarketCap(string memory emirate, uint256 marketCap) external onlyOwner {
        marketCapitalization[emirate] = marketCap;
        emit MarketDataUpdated("MARKET_CAP", emirate, marketCap);
    }

    /**
     * @dev Update 24h trading volume for a currency pair
     * @param currencyPair Currency pair (e.g., "AED_USD")
     * @param volume Trading volume
     */
    function updateTradingVolume(string memory currencyPair, uint256 volume) external onlyOwner {
        tradingVolume24h[currencyPair] = volume;
        emit MarketDataUpdated("VOLUME_24H", currencyPair, volume);
    }

    // =============================================================================
    // ADMIN FUNCTIONS
    // =============================================================================

    /**
     * @dev Add supported currency
     * @param currency Currency code
     * @param initialRate Initial exchange rate
     */
    function addSupportedCurrency(string memory currency, uint256 initialRate) external onlyOwner {
        _addSupportedCurrency(currency, initialRate);
    }

    /**
     * @dev Internal function to add supported currency
     */
    function _addSupportedCurrency(string memory currency, uint256 initialRate) private {
        require(!currencySupported[currency], "Currency already supported");
        require(initialRate > 0, "Invalid initial rate");

        currencySupported[currency] = true;
        supportedCurrencies.push(currency);

        exchangeRates[currency] = ExchangeRate({
            rate: initialRate,
            lastUpdated: block.timestamp,
            isActive: true,
            updater: owner()
        });

        emit ExchangeRateUpdated(currency, initialRate, block.timestamp);
    }

    /**
     * @dev Add authorized updater for exchange rates
     * @param updater Address of the updater
     * @param currency Currency they can update
     */
    function addAuthorizedUpdater(address updater, string memory currency) external onlyOwner {
        authorizedUpdaters[updater] = true;
        exchangeRates[currency].updater = updater;
        emit AuthorizedUpdaterAdded(updater, currency);
    }

    /**
     * @dev Remove authorized updater
     * @param updater Address to remove
     */
    function removeAuthorizedUpdater(address updater) external onlyOwner {
        authorizedUpdaters[updater] = false;
        emit AuthorizedUpdaterRemoved(updater);
    }

    /**
     * @dev Get all supported currencies
     * @return currencies Array of supported currency codes
     */
    function getSupportedCurrencies() external view returns (string[] memory currencies) {
        return supportedCurrencies;
    }

    /**
     * @dev Check if price data is fresh
     * @param currency Currency to check
     * @return isFresh Whether the price data is fresh
     */
    function isPriceFresh(string memory currency) external view validCurrency(currency) returns (bool isFresh) {
        ExchangeRate memory rateData = exchangeRates[currency];
        return block.timestamp.sub(rateData.lastUpdated) <= MAX_STALENESS;
    }

    /**
     * @dev Emergency pause function
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause function
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // =============================================================================
    // VIEW FUNCTIONS
    // =============================================================================

    /**
     * @dev Get oracle health status
     * @return totalCurrencies Number of supported currencies
     * @return activeCurrencies Number of active currencies
     * @return stalePrices Number of stale price feeds
     */
    function getOracleHealth() external view returns (
        uint256 totalCurrencies,
        uint256 activeCurrencies,
        uint256 stalePrices
    ) {
        totalCurrencies = supportedCurrencies.length;
        activeCurrencies = 0;
        stalePrices = 0;

        for (uint256 i = 0; i < supportedCurrencies.length; i++) {
            ExchangeRate memory rateData = exchangeRates[supportedCurrencies[i]];
            if (rateData.isActive) {
                activeCurrencies++;
            }
            if (block.timestamp.sub(rateData.lastUpdated) > MAX_STALENESS) {
                stalePrices++;
            }
        }
    }
}

contracts/UAE/UAEPropertyToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title UAEPropertyToken
 * @dev ERC1155 token contract for UAE real estate tokenization
 * @dev Compliant with RERA (Real Estate Regulatory Agency) requirements
 * @dev Supports multi-currency investment with AED as primary currency
 */
contract UAEPropertyToken is ERC1155, Ownable, Pausable, ReentrancyGuard {
    using Counters for Counters.Counter;
    using Strings for uint256;

    // =============================================================================
    // STATE VARIABLES
    // =============================================================================

    Counters.Counter private _tokenIds;

    // UAE-specific configuration
    string public constant JURISDICTION = "UAE";
    string public constant REGULATORY_AUTHORITY = "RERA";
    address public reraCompliance;
    address public dldRegistry; // Dubai Land Department

    // Property registry
    struct Property {
        uint256 tokenId;
        string reraRegistrationNumber;
        string dldTitleDeedNumber;
        string propertyAddress;
        string zone; // Dubai zone, Abu Dhabi area, etc.
        string emirate;
        uint256 totalValue; // in AED wei (18 decimals)
        uint256 totalSupply; // total tokens for this property
        uint256 pricePerToken; // in AED wei
        PropertyType propertyType;
        PropertyStatus status;
        address developer;
        uint256 createdAt;
        uint256 fundingDeadline;
        bool reraApproved;
        bool dldRegistered;
        mapping(address => uint256) investorBalances;
        mapping(string => bool) complianceChecks;
    }

    enum PropertyType {
        APARTMENT,
        VILLA,
        TOWNHOUSE,
        PENTHOUSE,
        OFFICE,
        RETAIL,
        WAREHOUSE,
        HOTEL_APARTMENT
    }

    enum PropertyStatus {
        PENDING_APPROVAL,
        FUNDRAISING,
        FUNDED,
        CONSTRUCTION,
        COMPLETED,
        GENERATING_INCOME,
        SOLD
    }

    enum InvestmentTier {
        RETAIL,     // 25,000 - 500,000 AED
        PREMIUM,    // 500,000 - 2,000,000 AED  
        INSTITUTIONAL // 2,000,000+ AED
    }

    // Property storage
    mapping(uint256 => Property) public properties;
    mapping(string => uint256) public reraToTokenId;
    mapping(string => uint256) public dldToTokenId;

    // Investment tracking
    mapping(address => mapping(uint256 => uint256)) public userInvestments;
    mapping(address => InvestmentTier) public userTiers;
    mapping(address => bool) public kycApproved;
    mapping(address => bool) public amlCleared;
    mapping(address => string) public userEmirates;

    // Currency support (all amounts stored in AED wei)
    mapping(string => bool) public supportedCurrencies;
    mapping(string => uint256) public exchangeRates; // to AED, 18 decimals

    // Dividend tracking
    mapping(uint256 => uint256) public totalDividendsPaid;
    mapping(uint256 => mapping(address => uint256)) public userDividendsClaimed;
    mapping(uint256 => uint256) public dividendPerToken;

    // UAE-specific features
    mapping(address => bool) public reraVerifiedDevelopers;
    mapping(address => bool) public uaeResidents;
    mapping(address => bool) public gccResidents;

    // =============================================================================
    // EVENTS
    // =============================================================================

    event PropertyListed(
        uint256 indexed tokenId,
        string reraNumber,
        string dldNumber,
        address indexed developer,
        uint256 totalValue,
        uint256 totalSupply
    );

    event PropertyInvestment(
        uint256 indexed tokenId,
        address indexed investor,
        uint256 amount,
        uint256 tokens,
        string currency
    );

    event PropertyFunded(
        uint256 indexed tokenId,
        uint256 totalRaised,
        uint256 totalInvestors
    );

    event DividendDistributed(
        uint256 indexed tokenId,
        uint256 totalAmount,
        uint256 perTokenAmount
    );

    event DividendClaimed(
        uint256 indexed tokenId,
        address indexed investor,
        uint256 amount
    );

    event RERAApprovalUpdated(
        uint256 indexed tokenId,
        bool approved,
        string registrationNumber
    );

    event DLDRegistrationUpdated(
        uint256 indexed tokenId,
        bool registered,
        string titleDeedNumber
    );

    event ComplianceCheckUpdated(
        uint256 indexed tokenId,
        string checkType,
        bool passed
    );

    // =============================================================================
    // MODIFIERS
    // =============================================================================

    modifier onlyRERACompliance() {
        require(msg.sender == reraCompliance, "Only RERA compliance officer");
        _;
    }

    modifier onlyKYCApproved() {
        require(kycApproved[msg.sender], "KYC approval required");
        _;
    }

    modifier onlyAMLCleared() {
        require(amlCleared[msg.sender], "AML clearance required");
        _;
    }

    modifier validProperty(uint256 tokenId) {
        require(tokenId > 0 && tokenId <= _tokenIds.current(), "Invalid property token");
        _;
    }

    modifier onlyPropertyFunding(uint256 tokenId) {
        require(properties[tokenId].status == PropertyStatus.FUNDRAISING, "Property not in fundraising");
        require(block.timestamp <= properties[tokenId].fundingDeadline, "Funding period ended");
        _;
    }

    // =============================================================================
    // CONSTRUCTOR
    // =============================================================================

    constructor(
        string memory _uri,
        address _reraCompliance,
        address _dldRegistry
    ) ERC1155(_uri) {
        reraCompliance = _reraCompliance;
        dldRegistry = _dldRegistry;

        // Initialize supported currencies
        supportedCurrencies["AED"] = true;
        supportedCurrencies["USD"] = true;
        supportedCurrencies["EUR"] = true;
        supportedCurrencies["GBP"] = true;
        supportedCurrencies["SAR"] = true;
        supportedCurrencies["QAR"] = true;
        supportedCurrencies["KWD"] = true;

        // Initial exchange rates (18 decimals, 1 AED = X)
        exchangeRates["AED"] = 1e18;
        exchangeRates["USD"] = 272e15; // 1 USD = ~3.67 AED
        exchangeRates["EUR"] = 246e15; // 1 EUR = ~4.06 AED
        exchangeRates["GBP"] = 214e15; // 1 GBP = ~4.67 AED
        exchangeRates["SAR"] = 272e15; // 1 SAR = ~0.98 AED
        exchangeRates["QAR"] = 101e16; // 1 QAR = ~1.01 AED
        exchangeRates["KWD"] = 83e15;  // 1 KWD = ~12.04 AED
    }

    // =============================================================================
    // PROPERTY MANAGEMENT
    // =============================================================================

    /**
     * @dev List a new property for tokenization
     * @param reraNumber RERA registration number
     * @param dldNumber DLD title deed number
     * @param propertyAddress Full property address
     * @param zone Dubai zone or area designation
     * @param emirate UAE emirate
     * @param totalValue Total property value in AED wei
     * @param totalSupply Total tokens to be issued
     * @param propertyType Type of property
     * @param developer Developer address
     * @param fundingDurationDays Funding period in days
     */
    function listProperty(
        string memory reraNumber,
        string memory dldNumber,
        string memory propertyAddress,
        string memory zone,
        string memory emirate,
        uint256 totalValue,
        uint256 totalSupply,
        PropertyType propertyType,
        address developer,
        uint256 fundingDurationDays
    ) external onlyOwner returns (uint256) {
        require(bytes(reraNumber).length > 0, "RERA number required");
        require(bytes(dldNumber).length > 0, "DLD number required");
        require(totalValue > 0, "Invalid total value");
        require(totalSupply > 0, "Invalid total supply");
        require(developer != address(0), "Invalid developer address");
        require(reraVerifiedDevelopers[developer], "Developer not RERA verified");

        _tokenIds.increment();
        uint256 newTokenId = _tokenIds.current();

        // Initialize property
        Property storage property = properties[newTokenId];
        property.tokenId = newTokenId;
        property.reraRegistrationNumber = reraNumber;
        property.dldTitleDeedNumber = dldNumber;
        property.propertyAddress = propertyAddress;
        property.zone = zone;
        property.emirate = emirate;
        property.totalValue = totalValue;
        property.totalSupply = totalSupply;
        property.pricePerToken = totalValue / totalSupply;
        property.propertyType = propertyType;
        property.status = PropertyStatus.PENDING_APPROVAL;
        property.developer = developer;
        property.createdAt = block.timestamp;
        property.fundingDeadline = block.timestamp + (fundingDurationDays * 1 days);
        property.reraApproved = false;
        property.dldRegistered = false;

        // Map RERA and DLD numbers to token ID
        reraToTokenId[reraNumber] = newTokenId;
        dldToTokenId[dldNumber] = newTokenId;

        emit PropertyListed(
            newTokenId,
            reraNumber,
            dldNumber,
            developer,
            totalValue,
            totalSupply
        );

        return newTokenId;
    }

    /**
     * @dev Approve property for fundraising (RERA compliance)
     */
    function approvePropertyRERA(
        uint256 tokenId,
        bool approved
    ) external onlyRERACompliance validProperty(tokenId) {
        properties[tokenId].reraApproved = approved;
        properties[tokenId].complianceChecks["RERA_APPROVED"] = approved;

        if (approved && properties[tokenId].dldRegistered) {
            properties[tokenId].status = PropertyStatus.FUNDRAISING;
        }

        emit RERAApprovalUpdated(tokenId, approved, properties[tokenId].reraRegistrationNumber);
        emit ComplianceCheckUpdated(tokenId, "RERA_APPROVED", approved);
    }

    /**
     * @dev Register property with DLD
     */
    function registerPropertyDLD(
        uint256 tokenId,
        bool registered
    ) external validProperty(tokenId) {
        require(msg.sender == dldRegistry || msg.sender == owner(), "Unauthorized DLD registration");

        properties[tokenId].dldRegistered = registered;
        properties[tokenId].complianceChecks["DLD_REGISTERED"] = registered;

        if (registered && properties[tokenId].reraApproved) {
            properties[tokenId].status = PropertyStatus.FUNDRAISING;
        }

        emit DLDRegistrationUpdated(tokenId, registered, properties[tokenId].dldTitleDeedNumber);
        emit ComplianceCheckUpdated(tokenId, "DLD_REGISTERED", registered);
    }

    // =============================================================================
    // INVESTMENT FUNCTIONS
    // =============================================================================

    /**
     * @dev Invest in a property with multi-currency support
     * @param tokenId Property token ID
     * @param tokenAmount Number of tokens to purchase
     * @param currency Currency code (AED, USD, EUR, etc.)
     * @param currencyAmount Amount in specified currency (wei)
     */
    function invest(
        uint256 tokenId,
        uint256 tokenAmount,
        string memory currency,
        uint256 currencyAmount
    ) external 
        onlyKYCApproved 
        onlyAMLCleared 
        validProperty(tokenId) 
        onlyPropertyFunding(tokenId)
        nonReentrant 
        whenNotPaused {

        require(supportedCurrencies[currency], "Currency not supported");
        require(tokenAmount > 0, "Invalid token amount");

        Property storage property = properties[tokenId];
        require(property.reraApproved && property.dldRegistered, "Property not approved");

        // Calculate required AED amount
        uint256 requiredAED = tokenAmount * property.pricePerToken;

        // Convert currency amount to AED
        uint256 aedEquivalent = (currencyAmount * exchangeRates[currency]) / 1e18;
        require(aedEquivalent >= requiredAED, "Insufficient amount");

        // Check investment limits based on user tier
        _checkInvestmentLimits(msg.sender, aedEquivalent);

        // Check token availability
        uint256 currentSupply = _getTotalSupply(tokenId);
        require(currentSupply + tokenAmount <= property.totalSupply, "Insufficient tokens available");

        // Mint tokens to investor
        _mint(msg.sender, tokenId, tokenAmount, "");

        // Update investment tracking
        userInvestments[msg.sender][tokenId] += aedEquivalent;
        property.investorBalances[msg.sender] += tokenAmount;

        // Check if property is fully funded
        if (_getTotalSupply(tokenId) >= property.totalSupply) {
            property.status = PropertyStatus.FUNDED;
            emit PropertyFunded(tokenId, property.totalValue, _getInvestorCount(tokenId));
        }

        emit PropertyInvestment(tokenId, msg.sender, aedEquivalent, tokenAmount, currency);
    }

    /**
     * @dev Check investment limits based on user tier and UAE regulations
     */
    function _checkInvestmentLimits(address investor, uint256 aedAmount) private view {
        InvestmentTier tier = userTiers[investor];

        if (tier == InvestmentTier.RETAIL) {
            require(aedAmount >= 25000 * 1e18, "Below minimum retail investment"); // 25,000 AED
            require(aedAmount <= 500000 * 1e18, "Above maximum retail investment"); // 500,000 AED
        } else if (tier == InvestmentTier.PREMIUM) {
            require(aedAmount >= 500000 * 1e18, "Below minimum premium investment"); // 500,000 AED
            require(aedAmount <= 2000000 * 1e18, "Above maximum premium investment"); // 2,000,000 AED
        } else if (tier == InvestmentTier.INSTITUTIONAL) {
            require(aedAmount >= 2000000 * 1e18, "Below minimum institutional investment"); // 2,000,000 AED
        }
    }

    // =============================================================================
    // DIVIDEND FUNCTIONS
    // =============================================================================

    /**
     * @dev Distribute dividends to token holders
     * @param tokenId Property token ID
     * @param totalDividend Total dividend amount in AED wei
     */
    function distributeDividends(
        uint256 tokenId,
        uint256 totalDividend
    ) external validProperty(tokenId) onlyOwner {
        require(totalDividend > 0, "Invalid dividend amount");
        require(properties[tokenId].status == PropertyStatus.GENERATING_INCOME, "Property not generating income");

        uint256 totalTokens = _getTotalSupply(tokenId);
        require(totalTokens > 0, "No tokens in circulation");

        uint256 dividendPerTokenAmount = totalDividend / totalTokens;

        dividendPerToken[tokenId] += dividendPerTokenAmount;
        totalDividendsPaid[tokenId] += totalDividend;

        emit DividendDistributed(tokenId, totalDividend, dividendPerTokenAmount);
    }

    /**
     * @dev Claim accumulated dividends
     * @param tokenId Property token ID
     */
    function claimDividends(uint256 tokenId) external validProperty(tokenId) nonReentrant {
        uint256 userTokens = balanceOf(msg.sender, tokenId);
        require(userTokens > 0, "No tokens owned");

        uint256 totalDividendPerToken = dividendPerToken[tokenId];
        uint256 userClaimedPerToken = userDividendsClaimed[tokenId][msg.sender];

        require(totalDividendPerToken > userClaimedPerToken, "No dividends to claim");

        uint256 unclaimedPerToken = totalDividendPerToken - userClaimedPerToken;
        uint256 dividendAmount = userTokens * unclaimedPerToken;

        userDividendsClaimed[tokenId][msg.sender] = totalDividendPerToken;

        // Transfer dividend (in native token or USDC/USDT)
        // Implementation depends on chosen dividend token

        emit DividendClaimed(tokenId, msg.sender, dividendAmount);
    }

    // =============================================================================
    // KYC/AML FUNCTIONS
    // =============================================================================

    /**
     * @dev Approve user KYC
     */
    function approveKYC(address user, bool approved) external onlyOwner {
        kycApproved[user] = approved;
    }

    /**
     * @dev Clear user AML
     */
    function clearAML(address user, bool cleared) external onlyOwner {
        amlCleared[user] = cleared;
    }

    /**
     * @dev Set user investment tier
     */
    function setUserTier(address user, InvestmentTier tier) external onlyOwner {
        userTiers[user] = tier;
    }

    /**
     * @dev Set user emirate
     */
    function setUserEmirate(address user, string memory emirate) external onlyOwner {
        userEmirates[user] = emirate;
    }

    /**
     * @dev Verify developer with RERA
     */
    function verifyDeveloper(address developer, bool verified) external onlyRERACompliance {
        reraVerifiedDevelopers[developer] = verified;
    }

    // =============================================================================
    // CURRENCY FUNCTIONS
    // =============================================================================

    /**
     * @dev Update exchange rates
     */
    function updateExchangeRate(string memory currency, uint256 rate) external onlyOwner {
        require(supportedCurrencies[currency], "Currency not supported");
        exchangeRates[currency] = rate;
    }

    /**
     * @dev Add supported currency
     */
    function addSupportedCurrency(string memory currency, uint256 initialRate) external onlyOwner {
        supportedCurrencies[currency] = true;
        exchangeRates[currency] = initialRate;
    }

    // =============================================================================
    // VIEW FUNCTIONS
    // =============================================================================

    /**
     * @dev Get property details
     */
    function getPropertyDetails(uint256 tokenId) external view validProperty(tokenId) returns (
        string memory reraNumber,
        string memory dldNumber,
        string memory propertyAddress,
        string memory zone,
        string memory emirate,
        uint256 totalValue,
        uint256 totalSupply,
        uint256 pricePerToken,
        PropertyType propertyType,
        PropertyStatus status,
        address developer,
        bool reraApproved,
        bool dldRegistered
    ) {
        Property storage property = properties[tokenId];
        return (
            property.reraRegistrationNumber,
            property.dldTitleDeedNumber,
            property.propertyAddress,
            property.zone,
            property.emirate,
            property.totalValue,
            property.totalSupply,
            property.pricePerToken,
            property.propertyType,
            property.status,
            property.developer,
            property.reraApproved,
            property.dldRegistered
        );
    }

    /**
     * @dev Get user investment summary
     */
    function getUserInvestmentSummary(address user, uint256 tokenId) external view returns (
        uint256 tokensOwned,
        uint256 aedInvested,
        uint256 currentValue,
        uint256 unclaimedDividends
    ) {
        tokensOwned = balanceOf(user, tokenId);
        aedInvested = userInvestments[user][tokenId];
        currentValue = tokensOwned * properties[tokenId].pricePerToken;

        uint256 totalDividendPerToken = dividendPerToken[tokenId];
        uint256 userClaimedPerToken = userDividendsClaimed[tokenId][user];
        unclaimedDividends = tokensOwned * (totalDividendPerToken - userClaimedPerToken);
    }

    /**
     * @dev Get total supply of tokens for a property
     */
    function _getTotalSupply(uint256 tokenId) private view returns (uint256) {
        // For ERC1155, we need to track total supply separately
        // This is a simplified implementation
        return properties[tokenId].totalSupply - _getRemainingTokens(tokenId);
    }

    /**
     * @dev Get remaining tokens available for a property
     */
    function _getRemainingTokens(uint256 tokenId) private view returns (uint256) {
        // Calculate based on property total supply and current state
        // This is a simplified implementation for demonstration
        return 0;
    }

    /**
     * @dev Get total number of investors for a property
     */
    function _getInvestorCount(uint256 tokenId) private view returns (uint256) {
        // This would need to be tracked separately in a real implementation
        // For simplicity, we're not implementing the full tracking here
        return 0;
    }

    /**
     * @dev Convert currency amount to AED
     */
    function convertToAED(string memory currency, uint256 amount) external view returns (uint256) {
        require(supportedCurrencies[currency], "Currency not supported");
        return (amount * exchangeRates[currency]) / 1e18;
    }

    // =============================================================================
    // ADMIN FUNCTIONS
    // =============================================================================

    /**
     * @dev Update property status
     */
    function updatePropertyStatus(uint256 tokenId, PropertyStatus newStatus) external onlyOwner validProperty(tokenId) {
        properties[tokenId].status = newStatus;
    }

    /**
     * @dev Set RERA compliance address
     */
    function setRERACompliance(address _reraCompliance) external onlyOwner {
        reraCompliance = _reraCompliance;
    }

    /**
     * @dev Set DLD registry address
     */
    function setDLDRegistry(address _dldRegistry) external onlyOwner {
        dldRegistry = _dldRegistry;
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Override URI for metadata
     */
    function uri(uint256 tokenId) public view override returns (string memory) {
        require(tokenId > 0 && tokenId <= _tokenIds.current(), "Token does not exist");
        return string(abi.encodePacked(super.uri(tokenId), tokenId.toString()));
    }

    // =============================================================================
    // HOOKS
    // =============================================================================

    /**
     * @dev Hook for token transfers
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override whenNotPaused {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        // Additional checks for UAE compliance
        if (to != address(0) && from != address(0)) {
            require(kycApproved[to], "Recipient must be KYC approved");
            require(amlCleared[to], "Recipient must be AML cleared");
        }
    }
}

contracts/UAE/UAEStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**
 * @title UAEStaking
 * @dev Staking contract for UAE property tokens with tier-based rewards
 * @dev Supports multi-currency rewards and volume-based bonuses
 */
contract UAEStaking is Ownable, Pausable, ReentrancyGuard {
    using SafeMath for uint256;

    // =============================================================================
    // STATE VARIABLES
    // =============================================================================

    IERC1155 public propertyToken;
    IERC20 public rewardToken;

    uint256 public constant PRECISION = 1e18;
    uint256 public rewardDuration;
    uint256 public baseRewardRate; // Base APY in basis points (1000 = 10%)

    // Staking tiers with different rewards
    enum StakingTier {
        BRONZE,   // 0-100K AED staked
        SILVER,   // 100K-500K AED staked  
        GOLD,     // 500K-2M AED staked
        PLATINUM, // 2M+ AED staked
        DIAMOND   // 5M+ AED staked (institutional)
    }

    struct StakingInfo {
        uint256[] tokenIds;           // Property tokens staked
        uint256[] amounts;            // Amount of each token staked
        uint256 totalValueStaked;     // Total AED value staked
        uint256 stakingStartTime;     // When user started staking
        uint256 lastRewardClaim;      // Last reward claim timestamp
        uint256 accumulatedRewards;   // Unclaimed rewards
        StakingTier tier;             // Current staking tier
        uint256 tradingVolume30d;     // Trading volume last 30 days
        bool isActive;                // Staking status
    }

    struct TierConfig {
        uint256 minStakeAmount;       // Minimum AED to achieve tier
        uint256 rewardMultiplier;     // Reward multiplier (1000 = 1x)
        uint256 volumeBonusRate;      // Additional bonus per 1M AED volume
        bool unlocked;                // Tier availability
    }

    // User staking information
    mapping(address => StakingInfo) public stakingInfo;
    mapping(StakingTier => TierConfig) public tierConfigs;

    // Property token pricing (in AED wei)
    mapping(uint256 => uint256) public tokenPrices;

    // Trading volume tracking
    mapping(address => mapping(uint256 => uint256)) public dailyVolume;
    mapping(address => uint256) public lastVolumeUpdate;

    // Reward tracking
    uint256 public totalStaked;
    uint256 public totalRewardsDistributed;
    uint256 public rewardPool;

    // UAE-specific features
    mapping(address => bool) public uaeResidents;
    mapping(address => string) public userEmirates;
    mapping(address => bool) public gccResidents;

    // Events
    event Staked(address indexed user, uint256[] tokenIds, uint256[] amounts, uint256 totalValue);
    event Unstaked(address indexed user, uint256[] tokenIds, uint256[] amounts, uint256 totalValue);
    event RewardsClaimed(address indexed user, uint256 amount);
    event TierUpgrade(address indexed user, StakingTier oldTier, StakingTier newTier);
    event VolumeRecorded(address indexed user, uint256 amount, uint256 newTotal);
    event RewardPoolIncreased(uint256 amount, uint256 newTotal);

    // =============================================================================
    // CONSTRUCTOR
    // =============================================================================

    constructor(
        address _propertyToken,
        address _rewardToken,
        uint256 _rewardDuration,
        uint256 _baseRewardRate
    ) {
        propertyToken = IERC1155(_propertyToken);
        rewardToken = IERC20(_rewardToken);
        rewardDuration = _rewardDuration;
        baseRewardRate = _baseRewardRate;

        // Initialize tier configurations
        _initializeTiers();
    }

    // =============================================================================
    // TIER INITIALIZATION
    // =============================================================================

    function _initializeTiers() private {
        // BRONZE: 0-100K AED
        tierConfigs[StakingTier.BRONZE] = TierConfig({
            minStakeAmount: 0,
            rewardMultiplier: 1000,  // 1x base rate
            volumeBonusRate: 50,     // 0.05% per 1M volume
            unlocked: true
        });

        // SILVER: 100K-500K AED  
        tierConfigs[StakingTier.SILVER] = TierConfig({
            minStakeAmount: 100000 * 1e18,
            rewardMultiplier: 1200,  // 1.2x base rate
            volumeBonusRate: 75,     // 0.075% per 1M volume
            unlocked: true
        });

        // GOLD: 500K-2M AED
        tierConfigs[StakingTier.GOLD] = TierConfig({
            minStakeAmount: 500000 * 1e18,
            rewardMultiplier: 1500,  // 1.5x base rate
            volumeBonusRate: 100,    // 0.1% per 1M volume
            unlocked: true
        });

        // PLATINUM: 2M-5M AED
        tierConfigs[StakingTier.PLATINUM] = TierConfig({
            minStakeAmount: 2000000 * 1e18,
            rewardMultiplier: 2000,  // 2x base rate
            volumeBonusRate: 150,    // 0.15% per 1M volume
            unlocked: true
        });

        // DIAMOND: 5M+ AED (Institutional)
        tierConfigs[StakingTier.DIAMOND] = TierConfig({
            minStakeAmount: 5000000 * 1e18,
            rewardMultiplier: 3000,  // 3x base rate
            volumeBonusRate: 200,    // 0.2% per 1M volume
            unlocked: true
        });
    }

    // =============================================================================
    // STAKING FUNCTIONS
    // =============================================================================

    /**
     * @dev Stake property tokens
     * @param tokenIds Array of property token IDs to stake
     * @param amounts Array of amounts for each token
     */
    function stake(
        uint256[] memory tokenIds,
        uint256[] memory amounts
    ) external nonReentrant whenNotPaused {
        require(tokenIds.length == amounts.length, "Array length mismatch");
        require(tokenIds.length > 0, "No tokens to stake");

        StakingInfo storage userStaking = stakingInfo[msg.sender];

        // Calculate total value being staked
        uint256 totalValue = 0;
        for (uint256 i = 0; i < tokenIds.length; i++) {
            require(amounts[i] > 0, "Invalid amount");
            require(tokenPrices[tokenIds[i]] > 0, "Token price not set");

            totalValue = totalValue.add(amounts[i].mul(tokenPrices[tokenIds[i]]));

            // Transfer tokens to this contract
            propertyToken.safeTransferFrom(
                msg.sender,
                address(this),
                tokenIds[i],
                amounts[i],
                ""
            );
        }

        // Update user staking info
        if (!userStaking.isActive) {
            userStaking.stakingStartTime = block.timestamp;
            userStaking.lastRewardClaim = block.timestamp;
            userStaking.isActive = true;
        }

        // Add to existing stakes
        for (uint256 i = 0; i < tokenIds.length; i++) {
            bool found = false;
            for (uint256 j = 0; j < userStaking.tokenIds.length; j++) {
                if (userStaking.tokenIds[j] == tokenIds[i]) {
                    userStaking.amounts[j] = userStaking.amounts[j].add(amounts[i]);
                    found = true;
                    break;
                }
            }
            if (!found) {
                userStaking.tokenIds.push(tokenIds[i]);
                userStaking.amounts.push(amounts[i]);
            }
        }

        userStaking.totalValueStaked = userStaking.totalValueStaked.add(totalValue);
        totalStaked = totalStaked.add(totalValue);

        // Update tier
        _updateUserTier(msg.sender);

        emit Staked(msg.sender, tokenIds, amounts, totalValue);
    }

    /**
     * @dev Unstake property tokens
     * @param tokenIds Array of property token IDs to unstake
     * @param amounts Array of amounts for each token
     */
    function unstake(
        uint256[] memory tokenIds,
        uint256[] memory amounts
    ) external nonReentrant {
        require(tokenIds.length == amounts.length, "Array length mismatch");
        require(tokenIds.length > 0, "No tokens to unstake");

        StakingInfo storage userStaking = stakingInfo[msg.sender];
        require(userStaking.isActive, "No active staking");

        // Claim pending rewards first
        _claimRewards(msg.sender);

        uint256 totalValue = 0;

        for (uint256 i = 0; i < tokenIds.length; i++) {
            require(amounts[i] > 0, "Invalid amount");

            // Find token in user's stakes
            bool found = false;
            for (uint256 j = 0; j < userStaking.tokenIds.length; j++) {
                if (userStaking.tokenIds[j] == tokenIds[i]) {
                    require(userStaking.amounts[j] >= amounts[i], "Insufficient staked amount");

                    uint256 value = amounts[i].mul(tokenPrices[tokenIds[i]]);
                    totalValue = totalValue.add(value);

                    userStaking.amounts[j] = userStaking.amounts[j].sub(amounts[i]);

                    // Remove token if amount becomes 0
                    if (userStaking.amounts[j] == 0) {
                        _removeTokenFromStaking(userStaking, j);
                    }

                    // Transfer tokens back to user
                    propertyToken.safeTransferFrom(
                        address(this),
                        msg.sender,
                        tokenIds[i],
                        amounts[i],
                        ""
                    );

                    found = true;
                    break;
                }
            }
            require(found, "Token not staked");
        }

        userStaking.totalValueStaked = userStaking.totalValueStaked.sub(totalValue);
        totalStaked = totalStaked.sub(totalValue);

        // Deactivate if no tokens left
        if (userStaking.tokenIds.length == 0) {
            userStaking.isActive = false;
        }

        // Update tier
        _updateUserTier(msg.sender);

        emit Unstaked(msg.sender, tokenIds, amounts, totalValue);
    }

    /**
     * @dev Remove token from staking arrays
     */
    function _removeTokenFromStaking(StakingInfo storage userStaking, uint256 index) private {
        require(index < userStaking.tokenIds.length, "Index out of bounds");

        userStaking.tokenIds[index] = userStaking.tokenIds[userStaking.tokenIds.length - 1];
        userStaking.amounts[index] = userStaking.amounts[userStaking.amounts.length - 1];

        userStaking.tokenIds.pop();
        userStaking.amounts.pop();
    }

    // =============================================================================
    // REWARD FUNCTIONS
    // =============================================================================

    /**
     * @dev Claim accumulated staking rewards
     */
    function claimRewards() external nonReentrant {
        _claimRewards(msg.sender);
    }

    /**
     * @dev Internal function to claim rewards
     */
    function _claimRewards(address user) private {
        StakingInfo storage userStaking = stakingInfo[user];
        require(userStaking.isActive, "No active staking");

        uint256 pendingRewards = calculatePendingRewards(user);

        if (pendingRewards > 0) {
            require(rewardPool >= pendingRewards, "Insufficient reward pool");

            userStaking.accumulatedRewards = userStaking.accumulatedRewards.add(pendingRewards);
            userStaking.lastRewardClaim = block.timestamp;
            rewardPool = rewardPool.sub(pendingRewards);
            totalRewardsDistributed = totalRewardsDistributed.add(pendingRewards);

            // Transfer rewards (assuming reward token is available)
            if (address(rewardToken) != address(0)) {
                rewardToken.transfer(user, pendingRewards);
            }

            emit RewardsClaimed(user, pendingRewards);
        }
    }

    /**
     * @dev Calculate pending rewards for a user
     */
    function calculatePendingRewards(address user) public view returns (uint256) {
        StakingInfo storage userStaking = stakingInfo[user];

        if (!userStaking.isActive || userStaking.totalValueStaked == 0) {
            return 0;
        }

        uint256 stakingDuration = block.timestamp.sub(userStaking.lastRewardClaim);
        if (stakingDuration == 0) {
            return 0;
        }

        TierConfig memory tierConfig = tierConfigs[userStaking.tier];

        // Base reward calculation (annual rate converted to per second)
        uint256 baseReward = userStaking.totalValueStaked
            .mul(baseRewardRate)
            .mul(tierConfig.rewardMultiplier)
            .mul(stakingDuration)
            .div(365 days)
            .div(10000)  // basis points
            .div(1000);  // multiplier precision

        // Volume bonus calculation
        uint256 volumeBonus = _calculateVolumeBonus(user, stakingDuration);

        // UAE resident bonus (additional 0.5% APY)
        uint256 uaeBonus = 0;
        if (uaeResidents[user]) {
            uaeBonus = userStaking.totalValueStaked
                .mul(50) // 0.5% in basis points
                .mul(stakingDuration)
                .div(365 days)
                .div(10000);
        }

        return baseReward.add(volumeBonus).add(uaeBonus);
    }

    /**
     * @dev Calculate volume-based bonus
     */
    function _calculateVolumeBonus(address user, uint256 duration) private view returns (uint256) {
        StakingInfo storage userStaking = stakingInfo[user];
        TierConfig memory tierConfig = tierConfigs[userStaking.tier];

        if (userStaking.tradingVolume30d == 0) {
            return 0;
        }

        // Bonus = (volume in millions) * tier rate * staked amount * duration
        uint256 volumeInMillions = userStaking.tradingVolume30d.div(1000000 * 1e18);

        return userStaking.totalValueStaked
            .mul(volumeInMillions)
            .mul(tierConfig.volumeBonusRate)
            .mul(duration)
            .div(365 days)
            .div(10000);
    }

    // =============================================================================
    // TIER MANAGEMENT
    // =============================================================================

    /**
     * @dev Update user's staking tier based on staked amount
     */
    function _updateUserTier(address user) private {
        StakingInfo storage userStaking = stakingInfo[user];
        StakingTier oldTier = userStaking.tier;
        StakingTier newTier = StakingTier.BRONZE;

        uint256 stakedAmount = userStaking.totalValueStaked;

        if (stakedAmount >= tierConfigs[StakingTier.DIAMOND].minStakeAmount) {
            newTier = StakingTier.DIAMOND;
        } else if (stakedAmount >= tierConfigs[StakingTier.PLATINUM].minStakeAmount) {
            newTier = StakingTier.PLATINUM;
        } else if (stakedAmount >= tierConfigs[StakingTier.GOLD].minStakeAmount) {
            newTier = StakingTier.GOLD;
        } else if (stakedAmount >= tierConfigs[StakingTier.SILVER].minStakeAmount) {
            newTier = StakingTier.SILVER;
        }

        if (newTier != oldTier) {
            userStaking.tier = newTier;
            emit TierUpgrade(user, oldTier, newTier);
        }
    }

    // =============================================================================
    // TRADING VOLUME TRACKING
    // =============================================================================

    /**
     * @dev Record trading volume for rewards calculation
     * @param user User address
     * @param amount Trading amount in AED wei
     */
    function recordTradingVolume(address user, uint256 amount) external onlyOwner {
        StakingInfo storage userStaking = stakingInfo[user];

        // Update rolling 30-day volume
        uint256 today = block.timestamp / 1 days;
        uint256 lastUpdate = lastVolumeUpdate[user] / 1 days;

        // Reset volume if more than 30 days passed
        if (today > lastUpdate + 30) {
            userStaking.tradingVolume30d = amount;
        } else {
            userStaking.tradingVolume30d = userStaking.tradingVolume30d.add(amount);
        }

        lastVolumeUpdate[user] = block.timestamp;

        emit VolumeRecorded(user, amount, userStaking.tradingVolume30d);
    }

    // =============================================================================
    // ADMIN FUNCTIONS
    // =============================================================================

    /**
     * @dev Set token price for reward calculations
     */
    function setTokenPrice(uint256 tokenId, uint256 price) external onlyOwner {
        tokenPrices[tokenId] = price;
    }

    /**
     * @dev Add to reward pool
     */
    function addRewardPool(uint256 amount) external onlyOwner {
        require(rewardToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");
        rewardPool = rewardPool.add(amount);
        emit RewardPoolIncreased(amount, rewardPool);
    }

    /**
     * @dev Update tier configuration
     */
    function updateTierConfig(
        StakingTier tier,
        uint256 minStakeAmount,
        uint256 rewardMultiplier,
        uint256 volumeBonusRate,
        bool unlocked
    ) external onlyOwner {
        tierConfigs[tier] = TierConfig({
            minStakeAmount: minStakeAmount,
            rewardMultiplier: rewardMultiplier,
            volumeBonusRate: volumeBonusRate,
            unlocked: unlocked
        });
    }

    /**
     * @dev Set UAE resident status for bonus rewards
     */
    function setUAEResident(address user, bool isResident) external onlyOwner {
        uaeResidents[user] = isResident;
    }

    /**
     * @dev Set user emirate
     */
    function setUserEmirate(address user, string memory emirate) external onlyOwner {
        userEmirates[user] = emirate;
    }

    /**
     * @dev Pause/unpause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // =============================================================================
    // VIEW FUNCTIONS
    // =============================================================================

    /**
     * @dev Get user staking details
     */
    function getUserStakingInfo(address user) external view returns (
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        uint256 totalValueStaked,
        uint256 pendingRewards,
        StakingTier tier,
        uint256 tradingVolume30d
    ) {
        StakingInfo storage userStaking = stakingInfo[user];
        return (
            userStaking.tokenIds,
            userStaking.amounts,
            userStaking.totalValueStaked,
            calculatePendingRewards(user),
            userStaking.tier,
            userStaking.tradingVolume30d
        );
    }

    /**
     * @dev Get tier requirements and benefits
     */
    function getTierInfo(StakingTier tier) external view returns (
        uint256 minStakeAmount,
        uint256 rewardMultiplier,
        uint256 volumeBonusRate,
        bool unlocked
    ) {
        TierConfig memory config = tierConfigs[tier];
        return (
            config.minStakeAmount,
            config.rewardMultiplier,
            config.volumeBonusRate,
            config.unlocked
        );
    }

    /**
     * @dev Get contract statistics
     */
    function getContractStats() external view returns (
        uint256 totalStakedAmount,
        uint256 totalRewards,
        uint256 currentRewardPool,
        uint256 totalStakers
    ) {
        return (
            totalStaked,
            totalRewardsDistributed,
            rewardPool,
            0 // Would need to track this separately
        );
    }

    // =============================================================================
    // ERC1155 RECEIVER
    // =============================================================================

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes memory
    ) public virtual returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] memory,
        uint256[] memory,
        bytes memory
    ) public virtual returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }
}

